import React from 'react';

class SearchTask extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            tasks : []
        }
    }

    componentWillMount() {
        if (localStorage && localStorage.getItem("tasks")) {
            this.setState({
                tasks: JSON.parse(localStorage.getItem("tasks"))
            })
            this.props.receviceData(JSON.parse(localStorage.getItem("tasks")));
        }
    }
    
    genarateTasks() {
        var tasks = [
            {
                id: this.genarateID(),
                name: 'Learn Reactjs',
                status: 1
            },
            {
                id: this.genarateID(),
                name: 'Learn Java',
                status: 0
            },
            {
                id: this.genarateID(),
                name: 'Learn Oracle',
                status: 1
            }
        ]
        this.setState({
            tasks: tasks
        })
        this.props.receviceData(tasks);
        localStorage.setItem("tasks", JSON.stringify(tasks));
    }

    s4() {
        return Math.floor(new Date().getTime()*Math.random()).toString(16).substring(1);
    }

    genarateID() {
        return this.s4() + '-' + this.s4();
    }

	render() {
		return (
				
                <div>
                    
                    <div className="row">
                        <div className="col-xs-12 col-sm-12 col-md-12 col-lg-12">
                            <button type="button" className="btn btn-primary">Create Task</button>&nbsp;&nbsp;
                            <button type="button" className="btn btn-danger" onClick={this.genarateTasks.bind(this)}>Generate Task</button>
                        </div>
                    </div>
                    <br/>
                    <div className="row">
                        
                        <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">
                            
                        <div className="input-group">
                            <input type="text" className="form-control" placeholder="Search" id="txtSearch"/>
                            <div className="input-group-btn">
                            <button className="btn btn-primary">
                                <span className="glyphicon glyphicon-search"></span>
                            </button>
                            </div>
                        </div>
                            
                        </div>
                        
                        <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">
                            <div className="dropdown">
                                <button className="btn btn-primary dropdown-toggle" type="button" data-toggle="dropdown">Sort&nbsp;
                                <span className="fa fa-caret-square-o-down"></span></button>
                                <ul className="dropdown-menu">
                                    <li><a className="fa fa-sort-alpha-asc">&nbsp;&nbsp;A -> Z</a></li>
                                    <li><a className="fa fa-sort-alpha-desc">&nbsp;&nbsp;Z -> A</a></li>
                                    <hr/>
                                    <li><a href="#">Active</a></li>
                                    <li><a href="#">Inactive</a></li>
                                </ul>
                            </div>
                        
                        </div>
                    
                    </div>
                </div>
                
		);
	}
}
export default SearchTask;
