import React from 'react';
import './App.css';
import Header from './components/Header';
import Product from './components/Product';
import Location from './components/Location';
import ColorPicker from './components/colorpicker/ColorPicker';
import Reset from './components/colorpicker/Reset';
import Result from './components/colorpicker/Result';
import Size from './components/colorpicker/Size';

class App extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			color: 'red',
			size :12
		}
	}

	receviceColor(params) {
		this.setState({
			color:params
		});
	}

	receviceSize(params) {
		this.setState({
			size:params
		});
	}

	render() {
		return (

			<div className="container mt-5">

				<div className="row">
					<div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">

						<ColorPicker 
							color={this.state.color}
							size={this.state.size}
							onRecevice={this.receviceColor.bind(this)}
						/>

					</div>

					<div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">

						<Size size={this.state.size} onRecevice={this.receviceSize.bind(this)}/>
						<Reset/>
					</div>

				</div>

				<div className="row">
					<div className="col-xs-12 col-sm-12 col-md-12 col-lg-12">
						<Result color={this.state.color}/>
					</div>
				</div>


			</div>

		);
	}
}

export default App;
