import React from 'react';

class SearchTask extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            keyword:'',
            sort: {
                by: '',
                value:1
            }
        }
    }
    

    genarateTasks() {
        this.props.genarateTasks();
    }

    displayAdd() {
        this.props.receviceIsDisplay(!this.props.isDisplayAdd);
    }

    onChange = (event) => {
        var target = event.target;
        var name = target.name;
        var value = target.value;
        this.setState({
            [name]:value
        })
    }

    onSearch = () => {
        this.props.onSearch(this.state.keyword);
    }

    handleKeyDown = (e) => {
        if (e.key === 'Enter') {
            this.props.onSearch(this.state.keyword);
        }
    }

    onSort(by, value) {
        this.props.onSort(by, value);
        this.setState({
            by: by,
            value: value
        })
    }

	render() {
        var { sort } = this.state;
		return (
				
                <div>
                    
                    <div className="row">
                        <div className="col-xs-12 col-sm-12 col-md-12 col-lg-12">
                            <button type="button" className="btn btn-primary" onClick={this.displayAdd.bind(this)}>Create Task</button>&nbsp;&nbsp;
                            <button type="button" className="btn btn-danger" onClick={this.genarateTasks.bind(this)}>Generate Task</button>
                        </div>
                    </div>
                    <br/>
                    <div className="row">
                        
                        <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">
                            
                        <div className="input-group">
                            <input 
                                type="text" 
                                name="keyword" 
                                className="form-control" 
                                placeholder="Search" 
                                id="txtSearch" 
                                onChange={this.onChange}
                                onKeyDown={this.handleKeyDown}/>
                            <div className="input-group-btn">
                            <button className="btn btn-primary" onClick={this.onSearch}>
                                <span className="glyphicon glyphicon-search"></span>
                            </button>
                            </div>
                        </div>
                            
                        </div>
                        
                        <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">
                            <div className="dropdown">
                                <button className="btn btn-primary dropdown-toggle" type="button" data-toggle="dropdown">Sort&nbsp;
                                <span className="fa fa-caret-square-o-down"></span></button>
                                <ul className="dropdown-menu">
                                    <li 
                                        onClick={this.onSort.bind(this, "name", 1)}
                                        className=" sort-selected"
                                        >
                                        <button className="fa fa-sort-alpha-asc pointer button-link">&nbsp;&nbsp;A -> Z</button>
                                    </li>
                                    <li onClick={this.onSort.bind(this, "name", -1)}><button className="fa fa-sort-alpha-desc pointer button-link">&nbsp;&nbsp;Z -> A</button></li>
                                    <hr/>
                                    <li onClick={this.onSort.bind(this, "status", 1)}><button className="pointer button-link">Active</button></li>
                                    <li onClick={this.onSort.bind(this, "status", -1)}><button className="pointer button-link">Inactive</button></li>
                                </ul>
                            </div>
                        
                        </div>
                    
                    </div>
                </div>
                
		);
	}
}
export default SearchTask;
