import React from 'react';

class Result extends React.Component {

    setColor() {
        return {
            color: this.props.color
        }
    }

    render() {
        return (
            <div>
                <p>Color : red - Size : 12</p>
                <div id="content" style={this.setColor.bind(this)}>
                    Content demo: Tu dep trai
                </div>
            </div>
        )
    }

}

export default Result;
