import React from 'react';

class Add extends React.Component {

    displayAdd() {
        this.props.receviceIsDisplay(!this.props.isDisplayAdd);
    }

	render() {
		return (
            <div className="panel panel-warning">
                <div className="panel-heading">
                    <h3 className="panel-title">Create Task<span className="fa fa-times-circle pull-right pointer"></span></h3>
                    
                </div>
                <div className="panel-body">
                    <div className="form-group">
                        <label>Task name</label>
                        <input type="text" className="form-control" id="" placeholder="Input field"/>
                    </div>
                    <div className="form-group">
                        <label>Status</label>
                        
                        <select name="" id="input" className="form-control" required="required">
                            <option value={1}>Active</option>
                            <option value={0}>Inactive</option>
                        </select>
                        
                    </div>
                    <button className="btn btn-primary">Save</button>
                    
                </div>
            </div>
                
		);
	}
}
export default Add;
