import React from 'react';

class Product extends React.Component {
    showProduct(iphone) {
        if (iphone.isInventory) {
            return <div className="col-xs-3 col-sm-3 col-md-3 col-lg-3">
                    <div className="thumbnail">
                        <img data-src="" alt="Iphone X" src={iphone.image}></img>
                        <div className="caption">
                            <h3>{iphone.name}</h3>
                            <p>
                                {iphone.price}
                            </p>
                            <p>
                                <a href="#" className="btn btn-primary">Buy</a>
                                <a href="#" className="btn btn-default">Detail</a>
                            </p>
                        </div>
                    </div>
                </div>
        }
    };

    render() {
        var iphone = {
            id : 1,
            image : "https://cdn.tgdd.vn/Products/Images/42/114115/iphone-x-64gb-2-400x460.png",
            name: "Iphone X",
            price: "15.000.000",
            isInventory: true
        };

        var location = [
            {
                id : 1,
                name : "Mobile Hoang Hoa Tham",
                address : "179 Truong Cong Dinh"
            },
            {
                id : 2,
                name : "Mobile Hoang Hoa Hoan",
                address : "1909 Le Duan"
            },
            {
                id : 3,
                name : "Mobile Hoang Hoa Hau",
                address : "909 Tu Dep Trai"
            },
        ];
      return <div>
                {this.showProduct(iphone)}
            </div>;
    }
  }

export default Product;
