import React from 'react';
import './App.css';
import Header from './components/Header';
import Product from './components/Product'

function App() {
  var iphones = [
		{
			id : 1,
			image : "https://cdn.tgdd.vn/Products/Images/42/114115/iphone-x-64gb-2-400x460.png",
			name: "Iphone X",
			price: "15.000.000",
			isInventory: true
		},
		{
			id : 2,
			image : "https://cdn.tgdd.vn/Products/Images/42/114115/iphone-x-64gb-2-400x460.png",
			name: "Iphone XR",
			price: "16.000.000",
			isInventory: true
		},
		{
			id : 3,
			image : "https://cdn.tgdd.vn/Products/Images/42/114115/iphone-x-64gb-2-400x460.png",
			name: "Iphone XS",
			price: "17.000.000",
			isInventory: true
		}
	];
	var elementIphones = iphones.map((iphone, index) => {
		return <div key={index}>
			<Product iphone={iphone}/>
		</div>
	});
  return (
    <div className="App">
      <Header/>
      <div className="row">
            <div className="col-xs-12 col-sm-12 col-md-12 col-lg-12">
               {elementIphones}
            </div>
        </div>

        <div class="row">
          <div class="col-xs-6 col-sm-6 col-md-6 col-lg-6">
            <Location/>
          </div>
        </div>

    </div>
  );
}

export default App;
